package exercise;

public class FlowEx12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// i의 값을 출력
		for (int i = 1; i <= 5; i++) {

			System.out.println(i);

		} // for 끝

		System.out.println();

		// i의 값을 가로로 출력
		for (int i = 1; i <= 5; i++) {

			System.out.print(i);

		} // for 끝

	} // main 끝

} // class 끝
