package exercise;

public class FlowEx13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int sum = 0; // 합계 저장용 변수

		for (int i = 1; i <= 10; i++) {
			
			sum += i;
			System.out.printf("1부터 %2d까지의 합 : %2d%n", i, sum);
			
		} // for 끝

	} // main 끝

} // class 끝
